CKEDITOR.dialog.add("simplebox",function(t){return{title:"Edit Simple Box",minWidth:200,minHeight:100,contents:[{id:"info",elements:[{id:"align",type:"select",label:"Align",items:[[t.lang.common.notSet,""],[t.lang.common.alignLeft,"left"],[t.lang.common.alignRight,"right"],[t.lang.common.alignCenter,"center"]],setup:function(t){this.setValue(t.data.align)},commit:function(t){t.setData("align",this.getValue())}},{id:"width",type:"text",label:"Width",width:"50px",setup:function(t){this.setValue(t.data.width)},commit:function(t){t.setData("width",this.getValue())}}]}]}});